import React, { useState, useEffect } from 'react';
import { Container, Row, Col} from 'react-bootstrap';
import '../assets/styles/wedding-prenup.css'; // Import your custom CSS for styling
import SlickCarousel from './SlickCarousel';




const WeddingPrenup = () => {
  const [isVisible, setIsVisible] = useState(false); 
  useEffect(() => {
    const handleScroll = () => {
      const section = document.querySelector('.prenup-section');
      const sectionTop = section.getBoundingClientRect().top;
      const viewportHeight = window.innerHeight;
      
      if (sectionTop < viewportHeight * 0.75) {
        setIsVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className={`prenup-section ${isVisible ? 'visible' : ''}`}>
      <Container fluid id = "prenup" className="">
        <Row className="justify-content-center">
            <Col md={12} className="text-center">
            <h1 className="global-title-template"> Our Prenup </h1>
            </Col>
        </Row>
        <Row className="justify-content-center margin-top-55 margin-bottom-140 gallery-row ">
           
            {/* Carousel for Gallery */}
            <Col md={2} xs={12}>
            <Slider {...settings}>
                {imageList.map((image, index) => (
                <div key={index} onClick={() => handleImageClick(index)}>
                    <img src={image} alt={`Gallery image ${index}`} className="gallery-image" />
                </div>
                ))}
            </Slider>
            </Col>


        {/* Modal with image and background blur */}
        <Modal show={showModal} onHide={closeModal} centered>
            <Modal.Body>
            <div className="image-modal-container">
                <img src={imageList[currentIndex]} alt={`Full view ${currentIndex}`} className="modal-image" />
            </div>
            </Modal.Body>
        </Modal>
          
         </Row>
      </Container>
    </section>
  );
};

export default WeddingPrenup;
