import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Modal } from 'react-bootstrap';
import '../assets/styles/wedding-prenup.css'; // Import your custom CSS for styling
import ja1 from '../assets/images/gallery-img/1.jpeg';
import ja2 from '../assets/images/gallery-img/2.jpeg';
import ja3 from '../assets/images/gallery-img/3.jpeg';
import ja4 from '../assets/images/gallery-img/4.jpeg';
import ja5 from '../assets/images/gallery-img/5.jpeg';
import ja6 from '../assets/images/gallery-img/6.jpeg';
import ja7 from '../assets/images/gallery-img/7.jpeg';
import ja8 from '../assets/images/gallery-img/8.jpeg';
import ja9 from '../assets/images/gallery-img/9.jpeg';
import ja10 from '../assets/images/gallery-img/10.jpeg';
import Slider from 'react-slick';

const WeddingPrenup = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0); // State for the current image index
  const [showModal, setShowModal] = useState(false); // Modal visibility
  const [imageList] = useState([ja1, ja2, ja3, ja4, ja5, ja6, ja7, ja8, ja9, ja10]); // List of image URLs

  // Scroll effect for visibility
  useEffect(() => {
    const handleScroll = () => {
      const section = document.querySelector('.prenup-section');
      const sectionTop = section.getBoundingClientRect().top;
      const viewportHeight = window.innerHeight;

      if (sectionTop < viewportHeight * 0.75) {
        setIsVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Carousel settings
  const settings = {
    infinite: true,
    slidesToShow: 4,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000, // 3 seconds
    dots: true,
    centerMode: false, // Disable center mode to prevent odd layout
    responsive: [
      {
        breakpoint: 1200, // For medium-large screens (2 images per row)
        settings: {
          slidesToShow: 2,
        },
      },
      {
        breakpoint: 768, // For smaller screens (1 image per row)
        settings: {
          slidesToShow: 1,
        },
      },
    ],
  };

  // Show image in modal with background blur
  const handleImageClick = (index) => {
    setCurrentIndex(index);
    setShowModal(true);
  };

  const closeModal = () => setShowModal(false);

  useEffect(() => {
    // Automatically close modal after some time (optional)
    if (showModal) {
      const timer = setTimeout(closeModal, 5000); // Close after 5 seconds
      return () => clearTimeout(timer); // Clean up on component unmount
    }
  }, [showModal]);

  return (
    <section className={`prenup-section ${isVisible ? 'visible' : ''}`}>
      <Container fluid id="prenup">
        <Row className="justify-content-center margin-top-55 margin-bottom-140">
          {/* Carousel for Gallery */}
          <Col>
            <Slider {...settings}>
              {imageList.map((image, index) => (
                <div key={index} onClick={() => handleImageClick(index)}>
                  <img src={image} alt={`Gallery image ${index}`} className="gallery-image img-fluid" />
                </div>
              ))}
            </Slider>
          </Col>

          {/* Modal with image and background blur */}
          <Modal show={showModal} onHide={closeModal} centered>
            <Modal.Body>
              <div className="image-modal-container">
                <img src={imageList[currentIndex]} alt={`Full view ${currentIndex}`} className="modal-image img-fluid" />
              </div>
            </Modal.Body>
          </Modal>
        </Row>
      </Container>
    </section>
  );
};

export default WeddingPrenup;
