import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import '../assets/styles/wedding-map.css'; // Import your custom CSS for styling
import map from '../assets/images/wedding-assets/Map_Guide.png';


const WeddingMap = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const section = document.querySelector('.map-section');
      const sectionTop = section.getBoundingClientRect().top;
      const viewportHeight = window.innerHeight;
      
      if (sectionTop < viewportHeight * 0.75) {
        setIsVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className={`map-section ${isVisible ? 'visible' : ''}`}>
      <Container fluid id = "map">
        <Row className="justify-content-center">
          <Col md={12} className="text-center">
            <h1 className="global-title-template"> Map Guide </h1>
          </Col>
        </Row>
        <Row className="justify-content-center margin-top-55">
          <Col md={12} className="text-center">
            <div className ="position-relative map-container">
                <img src={map} alt="Responsive Image" className="img-fluid custom-image position-absolute" />
            </div>
          </Col>
        </Row>

        
      </Container>
    </section>
  );
};

export default WeddingMap;
