import React, { useState, useEffect } from 'react';
import { Row, Col, Modal, Button } from 'react-bootstrap';
import Slider from 'react-slick';
import '../assets/styles/slickcarousel.css';
import ja1 from '../assets/images/gallery-img/1.jpeg';
import ja2 from '../assets/images/gallery-img/2.jpeg';
import ja3 from '../assets/images/gallery-img/3.jpeg';
import ja4 from '../assets/images/gallery-img/4.jpeg';
import ja5 from '../assets/images/gallery-img/5.jpeg';
import ja6 from '../assets/images/gallery-img/6.jpeg';
import ja7 from '../assets/images/gallery-img/7.jpeg';
import ja8 from '../assets/images/gallery-img/8.jpeg';
import ja9 from '../assets/images/gallery-img/9.jpeg';
import ja10 from '../assets/images/gallery-img/10.jpeg';

const SlickCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0); // State for the current image index
  const [showModal, setShowModal] = useState(false); // Modal visibility
  const [imageList] = useState([
    ja1, ja2, ja3, ja4,
    ja5, ja6, ja7, ja8,
    ja9, ja10
  ]); // List of image URLs

  // Carousel settings
  const settings = {
    infinite: true,
    slidesToShow: 4,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000, // 3 seconds
    dots: true,
  };

  // Show image in modal with background blur
  const handleImageClick = (index) => {
    setCurrentIndex(index);
    setShowModal(true);
  };

  const closeModal = () => setShowModal(false);

  useEffect(() => {
    // Automatically close modal after some time (optional)
    if (showModal) {
      const timer = setTimeout(closeModal, 5000); // Close after 5 seconds
      return () => clearTimeout(timer); // Clean up on component unmount
    }
  }, [showModal]);

  return (
    <div className="gallery-container">
      <Row className="gallery-row">
        {/* Carousel for Gallery */}
        <Col md={6} xs={12}>
          <Slider {...settings}>
            {imageList.map((image, index) => (
              <div key={index} onClick={() => handleImageClick(index)}>
                <img src={image} alt={`Gallery image ${index}`} className="gallery-image" />
              </div>
            ))}
          </Slider>
        </Col>
      </Row>

      {/* Modal with image and background blur */}
      <Modal show={showModal} onHide={closeModal} centered>
        <Modal.Body>
          <div className="image-modal-container">
            <img src={imageList[currentIndex]} alt={`Full view ${currentIndex}`} className="modal-image" />
          </div>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default SlickCarousel;
