import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import '../assets/styles/wedding-timeline.css'; // Import your custom CSS for styling
import weddingring from '../assets/images/icons/wedding-rings.png';

const WeddingTimeline = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const section = document.querySelector('.timeline-section');
      const sectionTop = section.getBoundingClientRect().top;
      const viewportHeight = window.innerHeight;
      
      if (sectionTop < viewportHeight * 0.75) {
        setIsVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const events = [
    { id: 1, icon: weddingring, alt: 'Reception Icon' },
    { id: 2, icon: weddingring, alt: 'Cake Cutting Icon' },
    { id: 3, icon: weddingring, alt: 'First Dance Icon' }
  ];

  return (
    <section className={`timeline-section background-gray ${isVisible ? 'visible' : ''}`}>
      <Container fluid id = "schedule">
        <Row className="justify-content-center">
          <Col md={12} className="text-center">
            <h1 className="global-title-template"> Timeline </h1>
          </Col>
        </Row>
        <Row className="justify-content-center margin-top-55 margin-bottom-140 position-relative">
            <Col md={2}></Col>
            <Col md={8}>
                <Row>
                     {/* Horizontal Center Line */}
                    <div className="timeline-line"></div>         
                    {events.map((event, index) => (
                        <Col key={event.id} md={6} className="timeline-icon">
                        <img src={event.icon} alt={event.alt} className="timeline-img" />
                        <div className={`vertical-line ${index % 2 === 0 ? 'right' : 'left'}`}></div>
                        </Col>
                    ))}
                </Row>

            </Col>
            <Col md={2}></Col>
        </Row>
      </Container>
    </section>
  );
};

export default WeddingTimeline;
