import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Modal, Carousel } from 'react-bootstrap';
import '../assets/styles/wedding-prenup.css'; // Import your custom CSS for styling
import ja1 from '../assets/images/gallery-img/1.jpeg';
import ja2 from '../assets/images/gallery-img/2.jpeg';
import ja3 from '../assets/images/gallery-img/3.jpeg';
import ja4 from '../assets/images/gallery-img/4.jpeg';
import ja5 from '../assets/images/gallery-img/5.jpeg';
import ja6 from '../assets/images/gallery-img/6.jpeg';
import ja7 from '../assets/images/gallery-img/7.jpeg';
import ja8 from '../assets/images/gallery-img/8.jpeg';
import ja9 from '../assets/images/gallery-img/9.jpeg';
import ja10 from '../assets/images/gallery-img/10.jpeg';



const WeddingPrenup = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentImage, setCurrentImage] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  // Images for the gallery carousel
  const images = [
    ja1,ja2,ja3,ja4,ja5,ja6,ja7,ja8,ja9,ja10
  ];

  // Handle selecting image in carousel
  const handleSelect = (selectedIndex) => {
    setCurrentImage(selectedIndex);
  };

  // Open modal to view image at 100% size
  const handleImageClick = (image) => {
    setSelectedImage(image);
    setShowModal(true);
  };

  // Close the modal
  const handleClose = () => setShowModal(false);

 
  useEffect(() => {
    const handleScroll = () => {
      const section = document.querySelector('.prenup-section');
      const sectionTop = section.getBoundingClientRect().top;
      const viewportHeight = window.innerHeight;
      
      if (sectionTop < viewportHeight * 0.75) {
        setIsVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className={`prenup-section ${isVisible ? 'visible' : ''}`}>
      <Container fluid id = "prenup" className="">
        <Row className="justify-content-center">
            <Col md={12} className="text-center">
            <h1 className="global-title-template"> Our Prenup </h1>
            </Col>
        </Row>
        <Row className="justify-content-center margin-top-55 margin-bottom-140 ">
            <Col md={12} className="text-center justify-content-center"> 
            <Col md={6} sm={12}>
          <div className="gallery-container">
            <Carousel activeIndex={currentImage} onSelect={handleSelect} interval={3000} controls={true}>
              {images.map((image, index) => (
                        <Carousel.Item key={index}>
                        <img
                            src={image}
                            alt={`Gallery Image ${index + 1}`}
                            className="d-block w-100"
                            onClick={() => handleImageClick(image)} // Handle zoom effect
                            style={{ cursor: 'pointer' }}
                        />
                        </Carousel.Item>
                    ))}
                    </Carousel>
                </div>
                </Col>          
            </Col>
            
        </Row>

        {/* Modal to display image at 100% size */}
        <Modal show={showModal} onHide={handleClose}>
            <Modal.Header closeButton>
            <Modal.Title>Image View</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <img src={selectedImage} alt="Selected" className="d-block w-100" />
            </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
                Close
            </Button>
            </Modal.Footer>
        </Modal>
      </Container>
    </section>
  );
};

export default WeddingPrenup;
